mapper.select:
SELECT * FROM CART
where
    MEMBER_ID = #{memberId}
AND PRODUCT_ID = #{prdId};

mapper.update:
UPDATE CART


        QUANTITY = #{qty},


        QUANTITY = QUANTITY + 1,


WHERE MEMBER_ID = #{memberId}
AND PRODUCT_ID = #{prdId};

mapper.delete:
DELETE FROM CART WHERE PRODUCT_ID

    = #{prdId}



       in (#{c})
